import React, { useEffect, useState } from "react";
import "./Home.css";

import { data  } from "../Data";
import Sorting from "../sorting/sorting";
import Cart from "../Cart/Cart";
import { FC } from "react";
import { Button } from "reactstrap";
interface datademo
{
  id: number; Author: string; ActualPrice: string; Discountedage: string; Tags: string; Tittle: string; Discription: string;
};

const Home:FC= ()=> {


const {courses}=data;
const [cart,setCart]=useState<datademo[]>([]);

const saveCart=(data:datademo)=>
{
  const newcart=[...cart]
  newcart.push(data)
    setCart([...newcart]);

}
    return (
      <div className="row">
        <div className="column">
          {courses.map((data, key) => {
            return (
              <div className="stock-container" key={data.id}>
                <label className="title">{data.Tittle}</label>
                <label className="author">{data.Author}</label>
                <label className="price">{data.ActualPrice}</label>
                <label className="button">
                  <Button type="submit"  onClick={() => saveCart(data)}>Add To Cart</Button>
                </label>
              </div>
            );
          })}
        </div>
        <div>

        </div>
      </div>
    );
}
export default Home;
