import { Button } from "reactstrap";
import { Console } from "console";
import {ICourse} from '../Interface/ICourse'

const Home:FC= ()=> {

const {courses}=data;
const [cart,setCart]=useState<ICourse[]>([]);
  let name="sushant";
const saveCart=(data:ICourse)=>
{
  const newcart=[...cart]
  newcart.push(data)
    setCart([...newcart]);
    console.log(setCart.length);

}

    return (
      <div className="row">
        <div className="column">
          {courses.map((data, key) => {
            return (
              <div className="course-container" key={data.id}>
                <label className="title">{data.Tittle}</label>
                <button className="tag">{data.Tags}</button>
                <label className="author">{data.Author}</label>
                <label className="price">{data.ActualPrice}</label>
                 <button  className="addcartbutton"type="submit"  onClick={() => saveCart(data)}>Add To Cart</button>

              </div>
            );
          })}
        </div>
        <div>
         <Cart data={name}></Cart>
        </div>
      </div>
    );
}
export default Home;
