import React, { useEffect, useState } from "react";
import "./Home.css";
import { Course } from "../Data";
import Cart from "../Cart/Cart";
import { FC } from "react";
interface Course{
  Author: string; ActualPrice: string; Discountedage: string; Tags: string; Tittle: string; Discription: string;
}

const Home:FC= ()=> {

  const [cart,setCart]=useState<Course[]>([]);

  const saveCart=(data: { Author: string; ActualPrice: string; Discountedage: string; Tags: string; Tittle: string; Discription: string; })=>{
    setCart([...cart,data]);

  }
    return (
      <div className="row">
        <div className="column">
          {Course.map((data, key) => {
            return (
              <div className="stock-container">
                <label className="title">{data.Tittle}</label>
                <label className="author">{data.Author}</label>
                <label className="price">{data.ActualPrice}</label>
                <label className="button">
                  <input type="submit" value="add" onClick={() => saveCart(data)}/>
                </label>
              </div>
            );
          })}
        </div>
        <div>
    <Cart></Cart>
        </div>
      </div>
    );
}

export default Home;
