import React, { useEffect, useState } from "react";
import "./Home.css";

import { data } from "../Data";

import Cart from "../Cart/Cart";
import { FC } from "react";

import { ICourse } from "../Interface/ICourse";
import Mywishlist from "../wishlist/mywishlist";
const Home: FC = () => {
  const { courses } = data;
  const [cart, setCart] = useState<ICourse[]>([]);
  const name = "sushant";
  const saveCart = (data: ICourse) => {
    const newcart = [...cart];
    newcart.push(data);
    setCart([...newcart]);
    console.log(setCart.length);
  };
  const addtowishlist = (data: ICourse) => {
    const newcart = [...cart];
    newcart.push(data);
    setCart([...newcart]);
    console.log(setCart.length);
  };

  return (
    <div className="row">
      <div className="column">
        {courses.map((data, key) => {
          return (
            <div className="course-container">
              <label className="image"></label>
              <label className="title">{data.Tittle}</label>
              <button className="tag">{data.Tags}</button>
              <label className="author">{data.Author}</label>
              <label className="wishlist" onClick={()=>addtowishlist(data)}>wishlist</label>
              <label className="price">{data.ActualPrice}</label>
              <button
                className="addcartbutton"
                type="submit"
                onClick={() => saveCart(data)}
              >
                Add To Cart
              </button>
            </div>
          );
        })}
      </div>
<Mywishlist mywish={cart}/>
      <Cart data={cart} />
    </div>
  );
};
export default Home;
