import React, { useEffect, useState } from "react";
import "./Home.css";

import { data } from "../Data";
import Sorting from "../sorting/sorting";
import Cart from "../Cart/Cart";
import { FC } from "react";
import { Button } from "reactstrap";
import { Console } from "console";
import { ICourse } from "../Interface/ICourse";

const Home: FC = () => {
  const { courses } = data;
  const [cart, setCart] = useState<ICourse[]>([]);
  const name = "sushant";
  const saveCart = (data: ICourse) => {
    const newcart = [...cart];
    newcart.push(data);
    setCart([...newcart]);
    console.log(setCart.length);
  };

  return (
    <div>

        {courses.map((data, key) => {
          return (
            <div className="course-container">
              <label className="image"></label>
              <label className="title">{data.Tittle}</label>
              <button className="tag">{data.Tags}</button>
              <label className="author">{data.Author}</label>
              <label className="price">{data.ActualPrice}</label>
              <button
                className="addcartbutton"
                type="submit"
                onClick={() => saveCart(data)}
              >
                Add To Cart
              </button>
            </div>
          );
        })}


      <Cart data={cart} />
    </div>
  );
};
export default Home;
